    <script>
        if (screen.width < 600) {
            $('.for-mobile').removeAttr("src");
            console.log('remove');
            $('.hambager-list').remove();
            $('.remove-style').removeAttr("style");
            $('.mobile-hidden').addClass('d-none');
			$('.mobile-wrap-title').html('Hair Extentions');
        }
        const toggleOnOffBtn = (id,whichOne) =>
        {
            console.log(id + whichOne);
            if (whichOne == 'remove')
            {
                $(`.expend-text${id}`).addClass('d-none');
                $(`.expend-click-${id}`).attr("onclick",`toggleOnOffBtn(${id},'add')`);
                $(`.expend-icon-${id}`).html("add");
            }else {
                $(`.expend-text${id}`).removeClass('d-none');
                $(`.expend-click-${id}`).attr("onclick",`toggleOnOffBtn(${id},'remove')`);
                $(`.expend-icon-${id}`).html("remove");
            }
        }
        
        const wpPrefix = "<?php echo get_template_directory_uri(); ?>";

        
        enterMouse = (id) =>
        {
            let originalSrc = $('.head-pic-'+id).attr('src');
            const hoverSrc = `${wpPrefix}/assets/images/head/hover_${id}.png`;
            const hoverSecondSrc = `${wpPrefix}/assets/images/head/second_hover${id}.png`;
			setTimeout(function (){
                $('.head-pic-'+id).attr('data-original-pic',originalSrc);
            },1200);

            $('.head-pic-'+id).attr('src',hoverSrc);
            setTimeout(function (){
                $('.head-pic-'+id).attr('src',hoverSecondSrc);
            },1200);
        }

        leaveMouse = (id) =>
        {
            let originalSrc = $('.head-pic-'+id).attr('data-original-pic');
//             console.log('leaving ' + originalSrc);
            // const hoverSrc = `assets/images/head/hover_${id}.png`;

            $('.head-pic-'+id).attr('src',originalSrc);
        }

    </script>

</body>
</html>